import React from "react";

interface ProductCardProps {
  product: any;
}

const ProductCard: React.FC<ProductCardProps> = ({ product }) => {
  // 가격 랜덤 생성 (실제로는 API에서 받아올 데이터)
  const price = Math.floor(Math.random() * 50000) + 20000;
  const discount = Math.random() > 0.7 ? Math.floor(Math.random() * 30) + 10 : 0;
  const finalPrice = discount > 0 ? Math.floor(price * (1 - discount / 100)) : price;

  return (
    <div className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 border border-gray-100 overflow-hidden group">
      {/* 이미지 */}
      <div className="relative overflow-hidden">
        <img 
          src={product.images[0]} 
          alt={product.name} 
          className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
          onError={(e) => {
            e.currentTarget.src = 'https://via.placeholder.com/400x300/4F46E5/FFFFFF?text=Tour+Image';
          }}
        />
        {/* 할인 배지 */}
        {discount > 0 && (
          <div className="absolute top-3 left-3 bg-red-500 text-white text-xs font-bold px-2 py-1 rounded-full">
            {discount}% 할인
          </div>
        )}
        {/* 위치 배지 */}
        <div className="absolute top-3 right-3 bg-blue-600 text-white text-xs font-bold px-2 py-1 rounded-full">
          {product.locations[0]}
        </div>
      </div>

      {/* 내용 */}
      <div className="p-4">
        {/* 카테고리 */}
        <div className="flex gap-1 mb-2">
          {product.categories.slice(0, 2).map((category: string, idx: number) => (
            <span key={idx} className="text-xs bg-blue-100 text-blue-600 px-2 py-1 rounded-full">
              {category}
            </span>
          ))}
        </div>

        {/* 제목 */}
        <h2 className="text-lg font-bold text-gray-900 mb-2 line-clamp-2">
          {product.name}
        </h2>

        {/* 설명 */}
        <p className="text-sm text-gray-600 mb-3 line-clamp-2">
          {product.description}
        </p>

        {/* 가격 */}
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center gap-2">
            {discount > 0 && (
              <span className="text-sm text-gray-400 line-through">
                ₩{price.toLocaleString()}
              </span>
            )}
            <span className="text-xl font-bold text-blue-600">
              ₩{finalPrice.toLocaleString()}
            </span>
          </div>
          <span className="text-xs text-gray-500">1인당</span>
        </div>

        {/* 태그 */}
        <div className="flex flex-wrap gap-1">
          {product.tags.slice(0, 3).map((tag: string, idx: number) => (
            <span key={idx} className="text-xs text-gray-500 bg-gray-100 px-2 py-1 rounded">
              #{tag}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
